import React, { useState, useEffect } from 'react';

const Countdown = () => {
  const [totSec, settotSec] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const hours = Math.floor(totSec / 3600);
  const minutes = Math.floor((totSec % 3600) / 60);
  const seconds = totSec % 60;

  useEffect(() => {
    let interval;

    if (isPlaying && totSec > 0) {
      interval = setInterval(() => {
        settotSec((prevSeconds) => (prevSeconds > 0 ? prevSeconds - 1 : 0));
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [isPlaying, totSec]);

  const handleTogglePlayPause = () => {
    if (totSec > 0) {
      setIsPlaying((prevIsPlaying) => !prevIsPlaying);
    }
  };

  const handleMinutesChange = (event) => {
    const newMinutes = parseInt(event.target.value, 10);

    if (!isNaN(newMinutes)) {
      settotSec(newMinutes * 60);
    } else if (event.target.value === "") {
      settotSec(0);
    } else {

    }
  };

  return (
    <div>
      <div>
        <label>Minutes: </label>
        <input
          type="number"
          value={Math.floor(totSec / 60)}
          onChange={handleMinutesChange}
        />
      </div>
      <div>
        <p>
          {String(hours).padStart(2, '0')}:{String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
        </p>
      </div>
      <button onClick={handleTogglePlayPause}>
        {isPlaying ? 'Pause' : 'Play'}
      </button>
    </div>
  );
};

export default Countdown;
