import React, { useState, useEffect } from 'react';

const Countdown = () => {
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    let interval;

    if (isPlaying) {
      interval = setInterval(() => {
        if (seconds > 0) {
          setSeconds(seconds - 1);
        } else {
          if (minutes > 0) {
            setMinutes(minutes - 1);
            setSeconds(59);
          } else {
            if (hours > 0) {
              setHours(hours - 1);
              setMinutes(59);
              setSeconds(59);
            } else {
              clearInterval(interval);
              setIsPlaying(false);
            }
          }
        }
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [isPlaying, hours, minutes, seconds]);

  const handleStart = () => {
    if (!isPlaying && (hours > 0 || minutes > 0 || seconds > 0)) {
      setIsPlaying(true);
    }
  };

  const handleReset = () => {
    setIsPlaying(false);
    setHours(0);
    setMinutes(0);
    setSeconds(0);
  };

  const handlePause = () => {
    setIsPlaying(false);
  };

  const handleHoursChange = (event) => {
    const newHours = parseInt(event.target.value, 10);
    if (!isPlaying) {
      setHours(newHours);
    }
  };

  const handleMinutesChange = (event) => {
    const newMinutes = parseInt(event.target.value, 10);
    if (!isPlaying) {
      setMinutes(newMinutes);
    }
  };

  const handleSecondsChange = (event) => {
    const newSeconds = parseInt(event.target.value, 10);
    if (!isPlaying) {
      setSeconds(newSeconds);
    }
  };

  return (
    <div>
      <div>
        <label>Hours: </label>
        <input type="number" value={hours} onChange={handleHoursChange} disabled={isPlaying} />
      </div>
      <div>
        <label>Minutes: </label>
        <input type="number" value={minutes} onChange={handleMinutesChange} disabled={isPlaying} />
      </div>
      <div>
        <label>Seconds: </label>
        <input type="number" value={seconds} onChange={handleSecondsChange} disabled={isPlaying} />
      </div>
      <div>
        <p>
          {String(hours).padStart(2, '0')}:{String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
        </p>
      </div>
      <button onClick={handleStart} disabled={isPlaying}>
        Play
      </button>
      <button onClick={handleReset}>Reset</button>
      <button onClick={handlePause} disabled={!isPlaying}>
        Pause
      </button>
    </div>
  );
};

export default Countdown;
