import React, { useState, useEffect } from 'react';

const Countdown = () => {
  const [totalSeconds, setTotalSeconds] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const hours = Math.floor(totalSeconds / 3600);
  const minutes = Math.floor((totalSeconds % 3600) / 60);
  const seconds = totalSeconds % 60;

  useEffect(() => {
    let interval;

    if (isPlaying && totalSeconds > 0) {
      interval = setInterval(() => {
        setTotalSeconds((prevSeconds) => (prevSeconds > 0 ? prevSeconds - 1 : 0));
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [isPlaying, totalSeconds]);

  const handleStart = () => {
    if (!isPlaying && totalSeconds > 0) {
      setIsPlaying(true);
    }
  };

  const handlePause = () => {
    setIsPlaying(false);
  };

  const handleMinutesChange = (event) => {
    const newMinutes = parseInt(event.target.value, 10);

    if (!isNaN(newMinutes)) {
      setTotalSeconds(newMinutes * 60);
    } else if (event.target.value === "") {
      // If the input becomes an empty string (after deleting the last digit),
      // set totalSeconds to 0 to prevent NaN and allow backspace functionality.
      setTotalSeconds(0);
    } else {
      // Handle non-numeric input, you can set an error state or display a message
    }
  };

  return (
    <div>
      <div>
        <label>Minutes: </label>
        <input
          type="number"
          value={Math.floor(totalSeconds / 60)}
          onChange={handleMinutesChange}
          disabled={isPlaying}
        />
      </div>
      <div>
        <p>
          {String(hours).padStart(2, '0')}:{String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
        </p>
      </div>
      <button onClick={handleStart} disabled={isPlaying}>
        Play
      </button>
      <button onClick={handlePause} disabled={!isPlaying}>
        Pause
      </button>
    </div>
  );
};

export default Countdown;
